mapper.select:
SELECT
  *
FROM
  leaveForms
WHERE
  isDeleted=0

    AND leave_id=#{leave_id}


    AND account=#{account}


    AND applyTime=#{applyTime}

mapper.update:
UPDATE
  leaveForms


        state=#{state},


        processInstanceId=#{processInstanceId}





        leave_id=#{leave_id}


      AND  account=#{account}

mapper.select:
SELECT * FROM leaveforms WHERE isDeleted=0 AND state <>'编辑中'
AND account=#{account}
AND applyTime like concat('%',#{keyWord},'%')
AND state=#{keyWord}
order by applyTime desc limit #{start},#{rows}

SELECT count(1) from leaveforms WHERE isDeleted=0 AND state <>'编辑中'
AND account=#{account}
AND applyTime like concat('%',#{keyWord},'%')
AND state=#{keyWord}

SELECT * FROM leaveforms WHERE isDeleted=0 AND state ='编辑中'
AND account=#{account}
AND applyTime like concat('%',#{keyWord},'%')
AND state=#{keyWord}
order by applyTime desc limit #{start},#{rows}

SELECT count(1) from leaveforms WHERE isDeleted=0 AND state='编辑中'
AND account=#{account}
AND applyTime like concat('%',#{keyWord},'%')
AND state=#{keyWord}
