mapper.select:
SELECT

FROM
  messages
WHERE
  isDeleted=0
AND
  isRead=#{isRead}

    AND id=#{id}



    AND send_id=#{send_id}


    AND receive_id=#{receive_id}


    AND content LIKE CONCAT('%',#{content},'%')


    AND sendTime like CONCAT('%',#{sendTime},'%')

SELECT
FROM messages WHERE isDeleted=0
AND receive_id=#{account}
AND sendTime like binary CONCAT('%',#{keyWord},'%')
AND content like binary CONCAT('%',#{keyWord},'%')
order by sendTime desc limit #{start},#{rows}
